import React from "react";
import ChildDummyComponent from "./ChildDummyComponent";

export default class ParentDummyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: ""
    };
  }

  onChange = (value) => {
    this.setState({
      value: value
    });
  };


  render() {

    return (
      <div>
        <ChildDummyComponent
          onChange={this.onChange}
          length={5}
          maxLength={4}
        />
        <div>{this.state.value}</div>
        <button style={{background:'#fc8621', padding:'10px', border:'none', outline:'none'}} onSubmit={()=>{alert(this.state.value)}}>Submit</button>
      </div>
    );
  }
}
